nta.declaration:
// Place global declarations here.
const int boardLength = 6;
const int boardHeight = 6;

clock time;

int counter = 0;

const int OCCUPIED = 1;
const int FREE = 0;

typedef int[FREE,OCCUPIED] bit_t;
typedef int[0, boardLength - 1] xCoord_t;
typedef int[0, boardHeight - 1] yCoord_t;

bit_t board[boardLength][boardHeight];

template.declaration:
// Place local declarations here.

clock t;

void Mark() {
	int i;
	for (i = y; i < y + length; i++) {
		board[x][i] = OCCUPIED;
	}
}

void MoveNorth() {
	board[x][y + length - 1] = FREE;
	y--;
	Mark();
	counter++;
}

void MoveSouth() {
	board[x][y] = FREE;
	y++;
	Mark();
	counter++;
}

// Place local declarations here.

clock t;

void Mark() {
	int i;
	for (i = x; i < x + length; i++) {
		board[i][y] = OCCUPIED;
	}
}

void MoveEast() {
	board[x][y] = FREE;
	x++;
	Mark();
	counter++;
}

void MoveWest() {
	board[x + length - 1][y] = FREE;
	x--;
	Mark();
	counter++;
}

